CREATE TABLE IF NOT EXISTS saved_locations (id BINARY(16) NOT NULL PRIMARY KEY, world VARCHAR(255) NOT NULL, x DOUBLE PRECISION NOT NULL, y DOUBLE PRECISION NOT NULL, z DOUBLE PRECISION NOT NULL, yaw SINGLE NOT NULL, pitch SINGLE NOT NULL);
CREATE TABLE IF NOT EXISTS player_settings (id BINARY(16) NOT NULL PRIMARY KEY, home BINARY(16) NULL, last_location BINARY(16) NULL, CONSTRAINT fk_player_settings_home__id FOREIGN KEY (home) REFERENCES saved_locations(id) ON DELETE RESTRICT ON UPDATE RESTRICT, CONSTRAINT fk_player_settings_last_location__id FOREIGN KEY (last_location) REFERENCES saved_locations(id) ON DELETE RESTRICT ON UPDATE RESTRICT);
