CREATE TABLE IF NOT EXISTS economy_accounts (id BINARY(16) NOT NULL PRIMARY KEY, balance DOUBLE PRECISION DEFAULT 0.0 NOT NULL);
CREATE TABLE IF NOT EXISTS player_transactions (id BINARY(16) NOT NULL PRIMARY KEY, amount DOUBLE PRECISION NOT NULL, sender_id BINARY(16) NOT NULL, recipient_id BINARY(16) NOT NULL, "timestamp" TEXT DEFAULT '2025-02-09 04:33:24.582' NOT NULL, notified BOOLEAN DEFAULT 0 NOT NULL);
